    private void btnEnviarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEnviarActionPerformed
        int number = 0;
        if (!txtNome.getText().isEmpty() && !txtNumber.getText().isEmpty()) {
            try {
                number = Integer.valueOf(txtNumber.getText());
                TCPClient.initClient(txtNome.getText(), number);
            } catch (NumberFormatException ex) {
                System.out.println("Deu Erro");
                JOptionPane.showMessageDialog(this, "Deu Erro preenche direito");
            } catch (ClassNotFoundException ex) {
                Logger.getLogger(MemoryGame.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }//GEN-LAST:event_btnEnviarActionPerformed

